package com.test;

import io.quarkus.test.junit.QuarkusTest;
import io.restassured.http.ContentType;

import org.junit.jupiter.api.Test;

import static io.restassured.RestAssured.given;

import com.test.ImageResource.ImageProperties;
import com.test.ImageResource.ImageRequest;

@QuarkusTest
public class ImageResourceTest {

    private static final String TEST_IMAGE = "data:image/png;base64,";
    private static final String PATH = "image_test/img1.png";

    @Test
    public void testSave() {

    ImageRequest request = new ImageRequest();
    request.setContent(TEST_IMAGE);
    request.setPath(PATH);
    ImageProperties filePropertiesRequest = new ImageProperties();
    filePropertiesRequest.setHeight(100);
    request.setProperties(filePropertiesRequest);

    given()
        .contentType(ContentType.JSON)
        .body(request)
        .post("/save")
        .then()
        .statusCode(201);
    }

}